# Get all screenshots in the todo folder
files = os.listdir("./screenshots_todo")
screenshots = []
for i in files:
    if i.split(".")[1] == "png":
        screenshots.append(i)
# Add data to the database
for s in screenshots:
    current_image = cv2.imread('./screenshots_todo/{}'.format(s))
    current_text = pytesseract.image_to_string(current_image)
    local_time = current_text[current_text.find("it's")+5: current_text.find("it's")+10]
    local = current_text[current_text.find("in")+3: current_text.find(",")]
    temperature = current_text[current_text.find("°C")-4 : current_text.find("°C")]
    countries = []
    for c in country_list:
        if current_text.find(c) != -1:
            countries.append(c)
    if len(countries) == 1:
        countries.append(countries[0])
    date = datetime.datetime.strptime(time.ctime(os.path.getctime('./screenshots_todo/{}'.format(s))), "%a %b %d %H:%M:%S %Y")
    query = "INSERT INTO rounds (guess, answer, local, temperature, local_time, training_id, date) VALUES (%s, %s, %s, %s, %s, %s, %s)"
    info = (countries[0], countries[1], local, temperature, local_time, training, date)
    cursor.execute(query, info)

mydb.commit()

# Send screenshots to done folder
for s in screenshots:
    os.rename('./screenshots_todo/{}'.format(s), './screenshots_done/{}'.format(s))